<?php

namespace Database\Seeders;

use App\Models\Comment;
use Illuminate\Database\Seeder;

class CommentSeeder extends Seeder
{
    /**
     * Одноразовый сидер: ~300 фейковых комментариев
     * в стиле двачевских обсуждений про IT, LLM, вайбкодинг и безработных айтишников.
     */
    public function run(): void
    {
        $threads = $this->getThreads();

        foreach ($threads as $thread) {
            $root = Comment::create([
                'name' => $thread['name'],
                'message' => $thread['message'],
                'ip_address' => $this->randomIp(),
                'created_at' => $thread['date'],
                'updated_at' => $thread['date'],
            ]);

            foreach ($thread['replies'] as $reply) {
                $parent = $root;

                // Иногда ответ — на другой ответ (вложенность)
                if (isset($reply['reply_to_index'])) {
                    $children = $root->children()->get();
                    if ($children->count() > 0) {
                        $idx = min($reply['reply_to_index'], $children->count() - 1);
                        $parent = $children[$idx];
                    }
                }

                $child = new Comment([
                    'name' => $reply['name'],
                    'message' => $reply['message'],
                    'ip_address' => $this->randomIp(),
                    'created_at' => $reply['date'],
                    'updated_at' => $reply['date'],
                ]);
                $child->appendToNode($parent)->save();
            }
        }
    }

    private function randomIp(): string
    {
        return mt_rand(1, 223) . '.' . mt_rand(0, 255) . '.' . mt_rand(0, 255) . '.' . mt_rand(1, 254);
    }

